    const [stats, useStats] = useState()
    useEffect(() => {
        async function fetchData() {
            console.log('fatching data');
            const data = await fetch('https://covid19.mathdro.id/api').then(data => data.json())
        }
        fetchData()
    }, [])
}

function Stats() {
    return <div>
        <p>Stats:</p>
    </div>
}

export default function Index() {
    return <div>
        <Stats>hellos</Stats>
    </div>
}
